/* rizin - LGPL - Copyright 2022 - user */

#include &lt;string.h&gt;
#include &lt;rz_types.h&gt;
#include &lt;rz_lib.h&gt;
#include &lt;rz_asm.h&gt;
#include &lt;rz_analysis.h&gt;

#define NB_INST 2

typedef struct {
    const char *name;
    ut8 len;
} mycpu_op_t;

static mycpu_op_t mycpu_op[] = {
    /*00*/  { "nop",    2 }, //enough
    /*01*/  { "ret",    2 },
}

static int mycpu_anop(RzAnalysis *analysis, RzAnalysisOp *op, ut64 addr, const ut8 *data, int len) {
    if (data[0] &lt; NB_INST) {
        op-&gt;size = mycpu_op[data[0]].len;
        op-&gt;addr = addr;
        op-&gt;type = RZ_ANALYSIS_OP_TYPE_UNK;
        switch (data[0]) {
            case 0x00:
                op-&gt;type = RZ_ANALYSIS_OP_TYPE_NOP;
                break;
            case 0x01:
                op-&gt;type = RZ_ANALYSIS_OP_TYPE_RET;
                break;
        }
        return op-&gt;size;
    }
}

struct rz_analysis_plugin_t rz_analysis_plugin_mycpu = {
    .name = "mycpu",
    .desc = "MYCPU analysis plugin",
    .license = "LGPL3",
    .arch = RZ_SYS_ARCH_NONE,
    .bits = 16,
    .init = NULL,
    .fini = NULL,
    .op = &mycpu_anop,
    .set_reg_profile = NULL,
    .fingerprint_bb = NULL,
    .fingerprint_fcn = NULL,
    .diff_bb = NULL,
    .diff_fcn = NULL,
    .diff_eval = NULL
};

#ifndef RZ_PLUGIN_INCORE
RZ_API RzLibStruct rizin_plugin = {
    .type = RZ_LIB_TYPE_ANALYSIS,
    .data = &rz_analysis_plugin_mycpu,
    .version = RZ_VERSION
};
#endif
